def find_first_marker(str, n):

    for i in range(n, len(str)):
        substr = str[i - n : i]
        if len(set(substr)) == n:
            return i

    raise ValueError(" shouldn't get to end, market not found")


if __name__ == "__main__":

    input = ""

    print(find_first_marker(input, 4))
    print(find_first_marker(input, 14))
